import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from '@hello-pangea/dnd';
import Login from './Login';
import Register from './Register';
import './App.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(() => {
    return localStorage.getItem('isLoggedIn') === 'true';
  });

  const [todos, setTodos] = useState(() => {
    const savedTodos = localStorage.getItem('todos');
    return savedTodos ? JSON.parse(savedTodos) : [];
  });

  useEffect(() => {
    localStorage.setItem('todos', JSON.stringify(todos));
  }, [todos]);

  const [newTodo, setNewTodo] = useState('');
  const [editingId, setEditingId] = useState(null);
  const [editValue, setEditValue] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const tasksPerPage = 10;

  // ËÆ°ÁÆóÂΩìÂâçÈ°µÁöÑ‰ªªÂä°
  const indexOfLastTask = currentPage * tasksPerPage;
  const indexOfFirstTask = indexOfLastTask - tasksPerPage;
  const currentTasks = todos.slice(indexOfFirstTask, indexOfLastTask);
  const totalPages = Math.ceil(todos.length / tasksPerPage);

  const [showRegister, setShowRegister] = useState(false);
  const [showAdminPanel, setShowAdminPanel] = useState(false);
  const [activeTab, setActiveTab] = useState('tasks'); // 'tasks' or 'admin'

  const pendingUsers = JSON.parse(localStorage.getItem('pendingUsers') || '[]');

  const toggleTodo = (id) => {
    setTodos(todos.map(todo => 
      todo.id === id ? { ...todo, completed: !todo.completed } : todo
    ));
  };

  const deleteTodo = (id) => {
    setTodos(todos.filter(todo => todo.id !== id));
  };

  const saveEdit = (id) => {
    if (editValue.trim()) {
      setTodos(todos.map(todo =>
        todo.id === id ? { ...todo, text: editValue } : todo
      ));
      setEditingId(null);
      setEditValue('');
    }
  };

  const cancelEdit = () => {
    setEditingId(null);
    setEditValue('');
  };

  const handleDragEnd = (result) => {
    if (!result.destination) return;

    const items = Array.from(todos);
    const [reorderedItem] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reorderedItem);

    setTodos(items);
  };

  const handleLogout = () => {
    localStorage.removeItem('isLoggedIn');
    setIsLoggedIn(false);
  };

  const addTodo = (text) => {
    if (text.trim()) {
      const newTodo = {
        id: Date.now(),
        text: text,
        completed: false
      };
      setTodos([...todos, newTodo]);
      setNewTodo('');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      addTodo(newTodo);
    }
  };

  // ÂàÜÈ°µÂ§ÑÁêÜÂáΩÊï∞
  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  // Ê∑ªÂä†ÁÆ°ÁêÜÂëòÂÆ°Ê†∏ÂäüËÉΩ
  const handleApproveUser = (user) => {
    const users = JSON.parse(localStorage.getItem('users') || '[]');
    const pendingUsers = JSON.parse(localStorage.getItem('pendingUsers') || '[]');

    // Ê∑ªÂä†Âà∞Â∑≤ÊâπÂáÜÁî®Êà∑ÂàóË°®
    users.push(user);
    localStorage.setItem('users', JSON.stringify(users));

    // ‰ªéÂæÖÂÆ°Ê†∏ÂàóË°®‰∏≠ÁßªÈô§
    const updatedPending = pendingUsers.filter(u => u.username !== user.username);
    localStorage.setItem('pendingUsers', JSON.stringify(updatedPending));

    // Ê∑ªÂä†ÊàêÂäüÈÄöÁü•
    alert(`User ${user.username} has been approved successfully!`);
  };

  const handleRejectUser = (username) => {
    const pendingUsers = JSON.parse(localStorage.getItem('pendingUsers') || '[]');
    const updatedPending = pendingUsers.filter(u => u.username !== username);
    localStorage.setItem('pendingUsers', JSON.stringify(updatedPending));

    // Ê∑ªÂä†ÊãíÁªùÈÄöÁü•
    alert(`User ${username} has been rejected.`);
  };

  // ‰øÆÊîπÁÆ°ÁêÜÂëòÈù¢ÊùøÁªÑ‰ª∂
  const AdminPanel = () => {
    const pendingUsers = JSON.parse(localStorage.getItem('pendingUsers') || '[]');
    const currentUser = localStorage.getItem('currentUser');
    const isSuperAdmin = currentUser === 'Vincent';

    return (
      <div className="admin-panel">
        <h3>
          {isSuperAdmin ? (
            <>Pending Approvals ({pendingUsers.length})</>
          ) : (
            'Pending Approvals'
          )}
        </h3>
        {pendingUsers.length === 0 ? (
          <div className="no-pending">No pending registrations</div>
        ) : (
          pendingUsers.map(user => (
            <div key={user.username} className="pending-user">
              <div className="user-info">
                <strong>{user.username}</strong>
                {isSuperAdmin && (
                  <>
                    <div className="user-email">{user.email}</div>
                    <small>Requested: {new Date(user.requestDate).toLocaleDateString()}</small>
                  </>
                )}
              </div>
              {isSuperAdmin && (
                <div className="approval-buttons">
                  <button 
                    onClick={() => {
                      if (window.confirm(`Approve user ${user.username}?`)) {
                        handleApproveUser(user);
                      }
                    }} 
                    className="approve-btn"
                  >
                    Approve
                  </button>
                  <button 
                    onClick={() => {
                      if (window.confirm(`Reject user ${user.username}?`)) {
                        handleRejectUser(user.username);
                      }
                    }} 
                    className="reject-btn"
                  >
                    Reject
                  </button>
                </div>
              )}
            </div>
          ))
        )}
      </div>
    );
  };

  if (!isLoggedIn) {
    if (showRegister) {
      return <Register onBack={() => setShowRegister(false)} />;
    }
    return (
      <Login 
        onLogin={setIsLoggedIn} 
        onRegister={() => setShowRegister(true)}
      />
    );
  }

  return (
    <div className="app-container">
      <div className="header">
        <div className="header-content">
          <h1>Todo List</h1>
          {localStorage.getItem('currentUser') === 'Vincent' && (
            <div className="tabs">
              <button 
                className={`tab-btn ${activeTab === 'tasks' ? 'active' : ''}`}
                onClick={() => setActiveTab('tasks')}
              >
                <span className="tab-icon">üìù</span>
                Tasks
              </button>
              <button 
                className={`tab-btn ${activeTab === 'admin' ? 'active' : ''}`}
                onClick={() => setActiveTab('admin')}
              >
                <span className="tab-icon">üë•</span>
                Pending Approval
                {pendingUsers.length > 0 && (
                  <span className="pending-badge">{pendingUsers.length}</span>
                )}
              </button>
            </div>
          )}
        </div>
        <div className="header-actions">
          <div className="profile-section">
            <div className="profile-info">
              <div className="profile-icon">
                {localStorage.getItem('currentUser').charAt(0).toUpperCase()}
              </div>
              <div className="profile-details">
                <span className="username">
                  {localStorage.getItem('currentUser')}
                  {localStorage.getItem('currentUser') === 'Vincent' && 
                    <span className="admin-badge">Admin</span>
                  }
                </span>
              </div>
            </div>
            <button onClick={handleLogout} className="logout-btn">
              Logout
            </button>
          </div>
        </div>
      </div>

      <div className="tab-content">
        {localStorage.getItem('currentUser') === 'Vincent' && activeTab === 'admin' ? (
          <AdminPanel />
        ) : (
          <>
            <div className="add-todo">
              <input
                type="text"
                value={newTodo}
                onChange={(e) => setNewTodo(e.target.value)}
                placeholder="Add a new task"
                onKeyPress={handleKeyPress}
              />
              <button onClick={() => addTodo(newTodo)}>Add</button>
            </div>

            <div className="task-count">
              <span>{todos.length} tasks</span>
              <span className="task-completed">
                {todos.filter(todo => todo.completed).length} completed
              </span>
            </div>

            {/* Todo list content */}
            <DragDropContext onDragEnd={handleDragEnd}>
              <Droppable droppableId="todos">
                {(provided) => (
                  <div
                    {...provided.droppableProps}
                    ref={provided.innerRef}
                  >
                    {currentTasks.map((todo, index) => (
                      <Draggable 
                        key={todo.id} 
                        draggableId={todo.id.toString()} 
                        index={index}
                      >
                        {(provided, snapshot) => (
                          <div
                            ref={provided.innerRef}
                            {...provided.draggableProps}
                            className={`todo-item ${todo.completed ? 'completed' : ''} ${
                              snapshot.isDragging ? 'dragging' : ''
                            }`}
                            style={{
                              ...provided.draggableProps.style,
                            }}
                          >
                            <div className="todo-content">
                              <div className="drag-handle" {...provided.dragHandleProps}>
                                ‚ãÆ‚ãÆ
                              </div>
                              <label className="checkbox-container">
                                <input
                                  type="checkbox"
                                  checked={todo.completed}
                                  onChange={() => toggleTodo(todo.id)}
                                />
                                <span className="checkmark"></span>
                              </label>
                              {editingId === todo.id ? (
                                <div className="edit-form">
                                  <input
                                    type="text"
                                    value={editValue}
                                    onChange={(e) => setEditValue(e.target.value)}
                                    className="edit-input"
                                    autoFocus
                                  />
                                  <div className="edit-buttons">
                                    <button 
                                      onClick={() => saveEdit(todo.id)}
                                      className="save-button"
                                    >
                                      Save
                                    </button>
                                    <button 
                                      onClick={cancelEdit}
                                      className="cancel-button"
                                    >
                                      Cancel
                                    </button>
                                  </div>
                                </div>
                              ) : (
                                <>
                                  <span className={todo.completed ? 'completed-text' : ''}>
                                    {todo.text}
                                  </span>
                                  <div className="todo-actions">
                                    <button 
                                      onClick={() => setEditingId(todo.id)} 
                                      className="edit-btn"
                                    >
                                      ‚úé
                                    </button>
                                    <button 
                                      onClick={() => deleteTodo(todo.id)} 
                                      className="delete-btn"
                                    >
                                      üóëÔ∏è
                                    </button>
                                  </div>
                                </>
                              )}
                            </div>
                          </div>
                        )}
                      </Draggable>
                    ))}
                    {provided.placeholder}
                  </div>
                )}
              </Droppable>
            </DragDropContext>

            {/* Ê∑ªÂä†ÂàÜÈ°µÊéß‰ª∂ */}
            {todos.length > tasksPerPage && (
              <div className="pagination">
                <button
                  onClick={() => handlePageChange(currentPage - 1)}
                  disabled={currentPage === 1}
                  className="page-btn"
                >
                  ‚Üê
                </button>

                {[...Array(totalPages)].map((_, index) => (
                  <button
                    key={index + 1}
                    onClick={() => handlePageChange(index + 1)}
                    className={`page-btn ${currentPage === index + 1 ? 'active' : ''}`}
                  >
                    {index + 1}
                  </button>
                ))}

                <button
                  onClick={() => handlePageChange(currentPage + 1)}
                  disabled={currentPage === totalPages}
                  className="page-btn"
                >
                  ‚Üí
                </button>
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
}

export default App;
